mapper.select:
SELECT count(*) FROM post WHERE board_class = #{board_class}
AND contents_category = #{category}
AND (contents LIKE '%'||#{searchKeyword}||'%' OR title LIKE '%'||#{searchKeyword}||'%')
AND ${searchByWhat} LIKE '%'||#{searchKeyword}||'%'

SELECT post.rownumber, post.post_id, post.member_id, post.title, post.contents, to_char(post.reg_date, 'yyyy. MM. dd.'), post.board_class, post.contents_category, post.process, post.imgurl, post.views FROM ( SELECT rownum as rownumber, post_id, member_id, title, contents, reg_date, board_class, contents_category, process, imgurl, views FROM post WHERE board_class = #{search.board_class}
AND contents_category = #{search.category}
) post WHERE
post.rownumber BETWEEN #{pvo.start} AND #{pvo.end} ORDER BY post.rownumber DESC

SELECT post.rownumber, post.post_id, post.member_id, post.title, post.contents,to_char(post.reg_date, 'yyyy. MM. dd.'), post.board_class, post.contents_category, post.process, post.imgurl, post.views FROM ( SELECT rownum as rownumber, post_id, member_id, title, contents, reg_date, board_class, contents_category, process, imgurl, views FROM post WHERE board_class = #{search.board_class}
AND (contents LIKE '%'||#{search.searchKeyword}||'%' OR title LIKE '%'||#{search.searchKeyword}||'%')
AND ${search.searchByWhat} LIKE '%'||#{search.searchKeyword}||'%'
AND contents_category = #{search.category}
) post WHERE
post.rownumber BETWEEN #{pvo.start} AND #{pvo.end} ORDER BY post.rownumber DESC

mapper.update:
UPDATE post
SET
	title = #{title},
	contents = #{contents}
WHERE
	post_id = #{post_id}

UPDATE post
SET
	decl = #{report}
WHERE
	post_id = #{post_id}
