mapper.select:
SELECT count(*) FROM post WHERE board_class = #{board_class}
AND contents_category = #{category}
AND (contents LIKE '%'||#{searchKeyword}||'%' OR title LIKE '%'||#{searchKeyword}||'%')
AND ${searchByWhat} LIKE '%'||#{searchKeyword}||'%'

SELECT post.rownumber, post.post_id, member.member_id, post.comment_id, post.title, post.contents,to_char(post.reg_date, 'yyyy. MM. dd.'), to_char(post.edit_date, 'yyyy. MM. dd.'), post.board_class, post.contents_category, post.process FROM ( SELECT rownum as rownumber, post_id, member_id, comment_id, title, contents, reg_date, edit_date, board_class, contents_category, process FROM post WHERE board_class = #{search.board_class}
AND contents_category = #{search.category}
) post, member WHERE post.member_id = member.member_num_id
AND post.rownumber BETWEEN #{pvo.start} AND #{pvo.end} ORDER BY post.rownumber DESC

SELECT post.rownumber, post.post_id, member.member_id, post.comment_id, post.title, post.contents,to_char(post.reg_date, 'yyyy. MM. dd.'), to_char(post.edit_date, 'yyyy. MM. dd.'), post.board_class, post.contents_category, post.process FROM ( SELECT rownum as rownumber, post_id, member_id, comment_id, title, contents, reg_date, edit_date, board_class, contents_category, process FROM post WHERE board_class = #{search.board_class}
AND (contents LIKE '%'||#{search.searchKeyword}||'%' OR title LIKE '%'||#{search.searchKeyword}||'%')
AND ${search.searchByWhat} LIKE '%'||#{search.searchKeyword}||'%'
AND contents_category = #{search.category}
) post, member WHERE post.member_id = member.member_num_id
AND post.rownumber BETWEEN #{pvo.start} AND #{pvo.end} ORDER BY post.rownumber DESC
